var i = 0,
	sideLength = 25,
	tileSize = 7, 
	borderTiles = 10,
	backColor = 'rgb(0,200,200)', // background color
	dataStr = [];  // frame data

/* add frames as needed */
dataStr[0] = '';

dataStr[1] = 
'';

dataStr[2] = 
'';

/* convert frame data*/
for (var j = 0; j < dataStr.length; j++) {
 dataStr[j] = dataStr[j].split("");
}

/* Animation colors; Be sure to provide as many values as indicated in frames; 
Index 0 is always the background color. */ 
var mapColor = function (pixel) {
  var theColors =[backColor,'rgb(0,0,0)','rgb(235,2,10)',
	'rgb(240,80,80)','rgb(255,239,213)','rgb(255,218,185)'];
	return theColors[pixel];
}

function setup() {
	var s = (sideLength + borderTiles * 2) * tileSize;
	createCanvas(s, s);
	noStroke();
	background(backColor);
}

function draw() {
	var pixel;
	// Retrieve next frame
	var mapArray = dataStr[i%2];
	
  	var offset = borderTiles * tileSize;
  	translate(offset, offset);

	for (var y = 0; y < sideLength; y++) { 
		for (var x = 0; x < sideLength; x++) {
			pixel = parseInt(mapArray[x + y * sideLength]);  
			fill(mapColor(pixel));
			rect(x * tileSize, y * tileSize, tileSize, tileSize);
  		}
	}
	
	i++;
 	frameRate(5);
}
